import 'package:flutter/material.dart';import 'package:snap_clone/model/user.dart';import 'custom/chat_list.dart';class ChatHomePage extends StatelessWidget {  List<User> userData = [    User(        imageLink:            'https://www.freepngimg.com/thumb/snapchat/73765-ghost-snapchat-snap-logo-ghosts-inc.-thumb.png')  ];  @override  Widget build(BuildContext context) {    return Container(        decoration: BoxDecoration(            borderRadius: BorderRadius.all(              Radius.circular(15),            ),            color: Colors.white        ),        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        child: ClipRRect(            borderRadius: BorderRadius.circular(15.0),            child: Column(              mainAxisAlignment: MainAxisAlignment.start,              children: [                buildTopChatBar(context),                SizedBox(                  height: 0,                ),                ChatList(),              ],            )));  }  SafeArea buildTopChatBar(BuildContext context) => SafeArea(        child: Container(           // color: Colors.blue,            margin: EdgeInsets.only(top: 10, bottom: 0),            child: Row(              crossAxisAlignment: CrossAxisAlignment.start,              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Row(                  children: [                    circularAvtar(context, '${userData[0].imageLink}'),                    circularButton(context, Icons.search),                  ],                ),                Padding(                  padding: const EdgeInsets.only(top: 15.0),                  child: Text(                    'Chat',                    style: TextStyle(fontWeight: FontWeight.bold, fontSize: 20),                  ),                ),                Row(                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    circularButton(context, Icons.person_add),                    circularButton(context, Icons.mode_edit),                  ],                ),              ],            )),      );  Widget circularButton(BuildContext context, icon) => Container(       // color: Colors.cyan,        height: 40,        width: 60,        child: FlatButton(          onPressed: () {},          child: new Icon(            icon,            color: Colors.grey[700],            size: 25.0,          ),          shape: new CircleBorder(),          color: Colors.black26,        ),      );  Widget circularAvtar(BuildContext context, imageLink) => Container(       // color: Colors.cyan,        height: 40,        width: 60,        child: FlatButton(          onPressed: () {},          child: Image.network(            imageLink,            fit: BoxFit.fill,          ),          shape: new CircleBorder(),          color: Colors.black26,        ),      );}