import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class BottomBar extends StatefulWidget {  dynamic onUpdate;  int isSelectedIndex;  BottomBar(this.isSelectedIndex, this.onUpdate);  @override  _BottomBarState createState() => _BottomBarState();}class _BottomBarState extends State<BottomBar> {  @override  Widget build(BuildContext context) {    return Container(      color: Colors.black,      height: MediaQuery.of(context).size.height * .07,      width: MediaQuery.of(context).size.width,      child: Row(        mainAxisSize: MainAxisSize.min,        crossAxisAlignment: CrossAxisAlignment.end,        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: [          buildBarIcon(context, Icons.location_on, widget.isSelectedIndex==2 ?Colors.green[600]:Colors.grey, 2),          buildBarIcon(context, Icons.sms,  widget.isSelectedIndex==1 ? Colors.blue[600]:Colors.grey, 1),          buildBarIcon(context, Icons.photo_camera, widget.isSelectedIndex==0 ? Colors.yellow[600]:Colors.grey, 0),          buildBarIcon(              context, Icons.supervisor_account, widget.isSelectedIndex==3 ? Colors.purple[600]:Colors.grey, 3),          buildBarIcon(context, Icons.play_arrow, widget.isSelectedIndex==4 ? Colors.red[600]:Colors.grey, 4),        ],      ),    );  }  Widget buildBarIcon(BuildContext context, icon, color, int index,) {    return GestureDetector(      onTap: () {        widget.onUpdate(index);      },      child: Container(        key: Key(index.toString()),        //color: Colors.red,        width: MediaQuery.of(context).size.width * .15,        height: MediaQuery.of(context).size.height * .06,        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          crossAxisAlignment: CrossAxisAlignment.center,          mainAxisSize: MainAxisSize.min,          children: [            Flexible(child: Icon(              icon,              size: 25,              color: color,            )),            widget.isSelectedIndex == index                ? Icon(                    Icons.arrow_drop_up,                    size: 20,                    color: color,                  )                : Container(),          ],        ),      ),    );  }}