import 'package:flutter/material.dart';import 'package:snap_clone/model/user.dart';class ChatList extends StatelessWidget {  List<User> userData = [    User(        imageLink:            'https://www.freepngimg.com/thumb/snapchat/73765-ghost-snapchat-snap-logo-ghosts-inc.-thumb.png')  ];  @override  Widget build(BuildContext context) {    return Container(      height: MediaQuery.of(context).size.height * .83,      child: ListView.separated(        padding: EdgeInsets.symmetric(horizontal: 0,vertical: 0),          itemCount: 25,          itemBuilder: (BuildContext context, int index) {            return Container(             // color: Colors.yellow,              margin: EdgeInsets.symmetric(horizontal: 10),              height: MediaQuery.of(context).size.height * .09,              width: MediaQuery.of(context).size.width,              child: Row(                mainAxisAlignment: MainAxisAlignment.start,                children: [                  circularAvtarDown(context, '${userData[0].imageLink}'),                  Container(                    margin: EdgeInsets.symmetric(horizontal: 5),                    child: Column(                      mainAxisAlignment: MainAxisAlignment.center,                      crossAxisAlignment: CrossAxisAlignment.start,                      children: [                        Text(                          'Gaurav Raj',                          style: TextStyle(                              fontWeight: FontWeight.bold, fontSize: 15),                        ),                        SizedBox(                          height: 10,                        ),                        Row(                          children: [                            Icon(                              Icons.textsms,                              size: 10,                              color: Colors.grey[400],                            ),                            SizedBox(                              width: 5,                            ),                            Text(                              'Tap to chat',                              style: TextStyle(                                  color: Colors.grey[400], fontSize: 10),                            ),                          ],                        ),                      ],                    ),                  ),                ],              ),            );          },          separatorBuilder: (BuildContext, index) {            return Divider(              color: Colors.grey[100],              thickness: 1,              height: 3,            );          }),    );  }  Widget circularAvtarDown(BuildContext context, imageLink) => InkWell(      onTap: () {},      child: Container(        child: CircleAvatar(          radius: 24,          backgroundColor: Colors.blue[600],          child: CircleAvatar(            radius: 22,            backgroundColor: Colors.white,            child: CircleAvatar(              radius: 20,              backgroundColor: Colors.grey[200],              backgroundImage: NetworkImage(imageLink),            ),          ),        ),      ));}