import 'package:flutter/material.dart';import 'package:snap_clone/presentaion/cam_page.dart';import 'package:snap_clone/presentaion/chat_page.dart';import 'package:snap_clone/presentaion/dis_page.dart';import 'package:snap_clone/presentaion/map_page.dart';import 'package:snap_clone/presentaion/story_page.dart';import 'custom/bottom_bar.dart';class HomePage extends StatefulWidget {  var cameras;  HomePage(this.cameras);  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int isSelectedIndex = 0;  @override  void initState() {    // TODO: implement initState    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(backgroundColor: Colors.black,      body: Center(child: _getSelectedPage(),),      bottomNavigationBar: BottomBar(isSelectedIndex, _updateIndex),    );  }  _updateIndex(int index) {    setState(() {      isSelectedIndex= index;    });  }  _getSelectedPage() {    switch(isSelectedIndex){      case 0:        return CamHomePage(widget.cameras);      case 1:        return ChatHomePage();      case 2:        return MapHomePage();      case 3:        return StoryHomePage();      case 4:        return DisHomePage();    }  }}